package op1;

public class Operators1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 6,k;
		
		k = i++;
		k = ++i;//post increment
		System.out.println("Tha value of i = " +k);
		System.out.println("Tha value of i = " +i);
		System.out.println("Tha value of i = " +i);

	}

}
